import { useState } from "react";
import "./Form.css";
import { v4 as uuidv4 } from 'uuid';


const Form = (props) => {

  const handlerChange = (event) => {
    event.preventDefault();
    props.setFrmMovementState({...props.frmMovementState, [event.target.name]: event.target.value});
  };

  const add = () => {

   const reg = new RegExp('^[a-zA-Z0-9]+$');
   const numerico = new RegExp('^[0-9]+$');
   if 
   (props.frmHeaderState.initial_balance === ''){
    alert("El saldo inicial no debe tener un valor");
  }else if (props.frmMovementState.type_of_movement === '') {
    alert("Seleccione un tipo de movimiento");
  } else if (props.frmMovementState.name === '') {
    alert("Debe ingresar un tipo de movimiento ");
  }else if (!reg.test(props.frmMovementState.name)){
    alert("El nombre del tipo de movimiento puede tener letras y numeros");
  }else if(!numerico.test(props.frmMovementState.value)){
    props.setFrmMovementState({...props.frmMovementState,value: ''});
    alert("La cantidad no permite letras ni valores negativos");
  }else if(props.frmMovementState.value<=0){
    props.setFrmMovementState({...props.frmMovementState,value: ''});
    alert("La cantidad ingresada debe ser mayor a cero");
  }else if(props.frmMovementState.type_of_movement === 'expense'){
    if (parseFloat(props.frmHeaderState.final_balance) - parseFloat(props.frmMovementState.value) < 0){
      alert("El campo no recibe valores negativos");

    }
  }else{
    const new_movement = {
      id: uuidv4(),
      type_of_movement: props.frmMovementState.type_of_movement,
      name: props.frmMovementState.name,
      value: props.frmMovementState.value
    };



    props.setMovementesState([...props.movementsState, new_movement]);

    //Limpiamos
    props.setFrmMovementState({ type_of_movement: '', name: '', value: '', edit: false});

    alert("El registro del movimiento quedo bien");
  }


  }

  const update = () => {

    const reg = new RegExp('^[a-zA-Z0-9]+$');
    const numerico = new RegExp('^[0-9]+$');
    if 
    (props.frmHeaderState.initial_balance === '')
    {
     alert("El saldo inicial no debe ir vacio");
   }else if (props.frmMovementState.type_of_movement === '') {
     alert("El tipo de movimiento no debe ir vacio");
   } else if (props.frmMovementState.name === '') {
     alert("Campo nombre del moviento no debe ir vacio");
   }else if (!reg.test(props.frmMovementState.name)){
     alert("El campo tipo de movimiento puede contener numeros y letras");
   }else if(!numerico.test(props.frmMovementState.value)){
    props.setFrmMovementState({...props.frmMovementState,value: ''});
     alert("El campo no recibe letras ni valores negativos");
   }else if(props.frmMovementState.value<=0){
     props.setFrmMovementState({...props.frmMovementState,value: ''});
     alert("El valor debe ser mayor a cero");
   }else if(props.frmMovementState.type_of_movement === 'expense'){
     if (parseFloat(props.frmHeaderState.final_balance) - parseFloat(props.frmMovementState.value) < 0){
       //Limpiamos
     //props.setFrmMovementState({value: ''});
       alert("El campo no recibe valores negativos");
     //  throw new Error('No tienes suficiente dinero para registrar ese gasto');

     }
    }else{
    const tmp = props.movementsState.filter(item => item.id !== props.frmMovementState.id);

    const edit_movement = {
      id: props.frmMovementState.id,
      type_of_movement: props.frmMovementState.type_of_movement,
      name: props.frmMovementState.name,
      value: props.frmMovementState.value
    };

    tmp.push(edit_movement);

    props.setMovementesState(tmp);
    props.setFrmMovementState({ type_of_movement: '', name: '', value: '', edit: false, id: ''});

    props.setshowToast({...props.showToast, show: true, message: 'Movimiento actualizado exitosamente', type: 'success'})

  }
}





  return (
    <div className="grid-x">
      <div className="cell small-12 xform">
        <div className="grid-x">
          <div className="cell small-12 xheader">
            Registro
          </div>
        </div>
        <div className="grid-x grid-padding-y xbody">
          <div className="cell small-12">
            <br/>
            <div className="grid-x">
              <div className="cell small-12">
                <div className="grid-x grid-padding-x">
                  <div className="small-5 cell">
                    <label>Tipo de movimiento: </label>
                  </div>
                  <div className="small-7 cell">
                    <select name="type_of_movement" onChange={handlerChange} value={props.frmMovementState.type_of_movement} >
                      <option value={''}>Seleccione...</option>
                      <option value={'deposit'}>Ingreso</option>
                      <option value={'expense'}>Gasto</option>
                    </select>
                  </div>
                </div>
              </div>

              <br/>
              <br/>

              <div className="cell small-12">
                <div className="grid-x grid-padding-x">
                  <div className="small-5 cell">
                    <label>Nombre: </label>
                  </div>
                  <div className="small-7 cell">
                    <input type="text" name="name" onChange={handlerChange} value={props.frmMovementState.name} />
                  </div>
                </div>
              </div>

              <div className="cell small-12">
                <div className="grid-x grid-padding-x">
                  <div className="small-5 cell">
                    <label>Cantidad: </label>
                  </div>
                  <div className="small-7 cell">
                    <input type="text" name="value" value={props.frmMovementState.value} onChange={handlerChange} />
                  </div>
                </div>
              </div>

              <div className="cell small-4">
                <div className="grid-x grid-padding-x">
                  <div className="small-5 cell">
                    <button className="button secondary">Cancelar</button>
                  </div>
                  <div className="small-5 small-offset-1 cell">
                    {!props.frmMovementState.edit &&
                      <button className="button primary" onClick={add}>Guardar</button>
                    }
                    {props.frmMovementState.edit &&
                      <button className="button primary" onClick={update}>Actualizar</button>
                    }
                  </div>
                </div>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Form;
