import { useState, useEffect } from "react";
import "./Header.css";

const Header = (props) => {
  //const [initialState, setInitialState] = useState('');

  const handlerChange = (event) => {
    event.preventDefault();
    props.setFrmHeaderState({...props.frmHeaderState, [event.target.name]: event.target.value});
  };

  useEffect(() => {

    const tmp = props.frmHeaderState.initial_balance.replace(/[^0-9]+/g, '');
    props.setFrmHeaderState({...props.frmHeaderState, initial_balance: tmp});
  }, [props.frmHeaderState.initial_balance]);

  return (
    <div className="grid-y">
      <div className="cell small-8">
        <img src={require('../img/iconoapp.png')} width={100} />
        <h4 class="card-title">Ingresos Y Gastos PDP</h4>
      </div>
        <div className="grid-x">
          <div className="cell small-6 small-offset-6">
              <div className="cell small-10">
              <div className="grid-x grid-padding-x">
              <div  align ="right">
                <label>
                  Saldo inicial
                  <input type="text" name="initial_balance" onChange={handlerChange} value={props.frmHeaderState.initial_balance} />
                </label>
              </div>
              <div  align ="right">
              <div className="cell small-6">
                <label>
                  Saldo final
                  <input type="text" value={props.frmHeaderState.final_balance} disabled/>
                </label>  
                </div>
                </div> 
              </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
