import {  useEffect } from "react";
import "./diseño/Header.css";

const Header = (props) => {
  //const [initialState, setInitialState] = useState('');

  const controlador = (e) => {
    e.preventDefault();
    props.setHeaderEstado({...props.headerEstado, [e.target.nombre]: e.target.value});
  };

  useEffect(() => {

    const tmp = props.headerEstado.saldoInicial.replace(/[^0-9]+/g, '');
    props.setHeaderEstado({...props.headerEstado, saldoInicial: tmp});
  }, [props.headerEstado.saldoInicial]);

  return (
    <div className="grid-y">
      <div className="cell small-8">
        <img src={require('./img/iconoapp.png')} width={100} />
        <h4 class="card-title">Ingresos Y Gastos PDP</h4>
      </div>
        <div className="grid-x">
          <div className="cell small-6 small-offset-6">
              <div className="cell small-10">
              <div className="grid-x grid-padding-x">
              <div  align ="right">
                <label>
                  Saldo inicial
                  <input type="text" name="saldoInicial" onChange={controlador} value={props.headerEstado.saldoInicial} />
                </label>
              </div>

              <div className="cell small-6">
              <div  align ="right">
                <label>
                  Saldo final
                  <input type="text" value={props.headerEstado.saldoFinal} />
                </label>  
                </div>
                </div> 
              </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
