import { useState, useEffect } from "react";

import Header from './components/Header';
import Form from './components/Form';
import Movimiento from './components/Movimiento';


import './components/diseño/App.css';

const App = () => {

  const [showToast, setshowToast] = useState({show: false, message: '', type: ''});

  const [headerEstado, setHeaderEstado] = useState({ initial_balance: "", final_balance: "" });
  const [movimientoEstado, setmovimientoEstado] = useState({ type_of_movement: '', name: '', value: '', edit: false, id: ''});
  const [movimiento, setmovimiento] = useState([]);

  useEffect(() => {
    var valor = headerEstado.initial_balance !== '' ? parseFloat(headerEstado.initial_balance) : 0;

    for (const movement of movimiento){
      if (movement.type_of_movement == 'expense') {
        valor  -= parseFloat(movement.value);
      }

      if (movement.type_of_movement == 'deposit') {
        valor  += parseFloat(movement.value);
      }
    }

    setHeaderEstado({...headerEstado, final_balance: String(valor )});

  }, [headerEstado.initial_balance, movimiento]);

  return (
    <div className="grid-x grid-margin-y grid-padding-y app-content">



      <div className="cell small-8 small-offset-2 xcard">

        <div className="grid-x grid-margin-x">
          <div className="cell small-12">
            <Header
              headerEstado={headerEstado}
              setHeaderEstado={setHeaderEstado}
            />
          </div>
        </div>

        <br/>

        <div className="grid-x grid-padding-x">
          <div className="cell small-6">
            <Form
              movimiento={movimiento}
              setmovimiento={setmovimiento}
              showToast={showToast}
              setshowToast={setshowToast}
              headerEstado={headerEstado}
              setHeaderEstado={setHeaderEstado}
              movimientoEstado={movimientoEstado}
              setmovimientoEstado={setmovimientoEstado}
            />
          </div>

          <div className="cell small-6">
            <Movimiento
              movimiento={movimiento}
              setmovimiento={setmovimiento}
              movimientoEstado={movimientoEstado}
              setmovimientoEstado={setmovimientoEstado}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
