import { useEffect, useState } from "react";
import "./diseño/Movimiento.css";

import CambioMovimiento from './CambioMovimiento';


const Movimiento = (props) => {
  const [filterState, setFilter] = useState({type_of_movement: '', filter: ''});
  const [filteredMovementsState, setFilteredMovements] = useState([]);

  const controladorFilter = (e) => {
    setFilter({...filterState, [e.target.name]: e.target.value});
  }

  const eliminar = (id) => {
    const valor = props.movimiento.filter(item => item.id !== id);
    props.setmovimiento(valor);
  }

  const actualizar = (valor) => {
    item.edit = true;
    props.setmovimientoEstado(valor);
  }

  useEffect(() => {
    setFilteredMovements(props.movimiento.filter(item => item.name.includes(filterState.filter) && (item.type_of_movement == filterState.type_of_movement || filterState.type_of_movement == '') ));
  }, [filterState, props.movimiento]);

  return (

    <div className="grid-x">
      <div className="color"> 
      <div className="card mb-3 bg-light">
        <div className="grid-x">
          <div className="card-body ">
          Listado Movimientos 
            <span className="notification">{filteredMovementsState.length}</span>
          </div>
        </div>
        <div className="grid-x grid-padding-y xbody">
          <div className="cell small-12">
            <br/>
            <div className="grid-x grid-padding-x">
              <div className="cell small-6">
                <input type="text" name={'filter'} onChange={controladorFilter} />
              </div>
              <div className="cell small-6">
                <input type="radio" name="type_of_movement" onChange={controladorFilter} value={''}/> Todos
                &nbsp;
                <input type="radio" name="type_of_movement" onChange={controladorFilter} value={'expense'}/> Gastos
                &nbsp;
                <input type="radio" name="type_of_movement" onChange={controladorFilter} value={'deposit'}/> Ingresos
              </div>
            </div>
            <br/>

            {filteredMovementsState.map((movement) => (
              <CambioMovimiento
                movement={movement}
                key={movement.id}
                eliminar={eliminar}
                actualizar={actualizar}
              />
            ))}

          </div>
        </div>
        <br/>
      </div>
      </div>
    </div>
  );
};

export default Movimiento;
