import { useEffect, useState } from "react";
import "../diseño/Movements.css";

import MovementItem from './MovementItem';


const Movements = (props) => {
  const [filterState, setFilter] = useState({type_of_movement: '', filter: ''});
  const [filteredMovementsState, setFilteredMovements] = useState([]);

  const handleChangeFilter = (event) => {
    setFilter({...filterState, [event.target.name]: event.target.value});
  }

  const deleteItem = (id) => {
    const tmp = props.movementsState.filter(item => item.id !== id);
    props.setMovementesState(tmp);
  }

  const editItem = (item) => {
    item.edit = true;
    props.setFrmMovementState(item);
  }

  useEffect(() => {
    setFilteredMovements(props.movementsState.filter(item => item.name.includes(filterState.filter) && (item.type_of_movement == filterState.type_of_movement || filterState.type_of_movement == '') ));
  }, [filterState, props.movementsState]);

  return (
    <div className="grid-x">

      <div className="cell small-12 xform">
        <div className="grid-x">
          <div className="card-body">
            Listado Movimientos
            <span className="notification">{filteredMovementsState.length}</span>
          </div>
        </div>
        <div className="grid-x grid-padding-y xbody">
          <div className="cell small-12">
            <br/>
            <div className="grid-x grid-padding-x">
              <div className="cell small-6">
                <input type="text" name={'filter'} onChange={handleChangeFilter} />
              </div>
              <div className="cell small-6">
                <input type="radio" name="type_of_movement" onChange={handleChangeFilter} value={''}/> Todos
                &nbsp;
                <input type="radio" name="type_of_movement" onChange={handleChangeFilter} value={'expense'}/> Gastos
                &nbsp;
                <input type="radio" name="type_of_movement" onChange={handleChangeFilter} value={'deposit'}/> Ingresos
              </div>
            </div>
            <br/>

            {filteredMovementsState.map((movement) => (
              <MovementItem
                movement={movement}
                key={movement.id}
                deleteItem={deleteItem}
                editItem={editItem}
              />
            ))}

          </div>
        </div>
        <br/>
      </div>
    </div>
  );
};

export default Movements;
