import { useState } from "react";
import "./MovementItem.css";

import NumberFormat from 'react-number-format';

const MovementItem = (props) => {
  return (
      <div className="grid-x" key={props.movement.id}>
        <div className="cell small-12 movement-row">
          <div className="grid-x">
            <div className="small-8 cell">
              <span>
                <img src={require('../img/icon-delete.png')} width={15} onClick={() => props.deleteItem(props.movement.id)} />
                &nbsp;
                <img src={require('../img/icon-edit.png')} width={15} onClick={() => props.editItem(props.movement)} />
                &nbsp;
                {props.movement.name}
              </span>
            </div>
            <div className="small-4 cell">
              <div className={props.movement.type_of_movement == 'deposit' ? "movement-type movement-deposit" : 'movement-type movement-expense'}> 
                <NumberFormat value={props.movement.value} displayType={'text'} thousandSeparator={true} prefix={'$'} />
              </div>
            </div>
          </div>
        </div>
      </div>

  );
};

export default MovementItem;
