  return (
    <div className="grid-y">
      <div className="card mb-3 bg-light">
        <div className="grid-y">
          <div className="card-body">
            Registro
          </div>
        </div>
        <div className="grid-y grid-padding-y xbody">
          <div className="cell small-12">
            <br/>
            <div className="grid-x">
              <div className="cell small-14">

                  <div className="col-sm-6">
                    <label>Tipo de movimiento: </label>
                  </div>
                  <div className="small-7 cell">
                    <select name="type_of_movement" onChange={controlador} value={props.frmMovementState.type_of_movement} >
                      <option value={''}>Seleccione...</option>
                      <option value={'expense'}>Gasto</option>
                      <option value={'deposit'}>Ingreso</option>

                    </select>
